import React from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { deleteProduct } from "../store/products";
// import React, {useState, useEffect} from 'react'
// import {useParams, useNavigate} from 'react-router-dom'

const Product = ({ product }) => {
  const { title, price, category, image,id } = product;

  const dispatch = useDispatch();

  // const deleteCurrentProduct = () => {
  //   fetch(`http://localhost:8000/product/delete/${id}`, {
  //       method: "Post",
  //     });
  //     dispatch(deleteProduct(id))
 

  //   }
  const deleteCurrentProduct = async (id)=>{
    
    let result = await fetch (`http://localhost:8000/product/delete/${id}`,{
      method : 'Delete'      
    })
    result = await result.json()

    if(result){
      alert('product deleted')
      window.location.reload()
    }
  }
  // const updateProduct= async ()=>{
  //   console.warn(title,price,description,category)
  //   let result = await fetch(`http://localhost:8080/product/updateproduct/${params.id}`,{
  //     method:'Put',
  //     body:JSON.stringify({title,price,description,category}),
  //     headers:{
  //       'Content-Type':"application/json"
  //     }
  //   })
  //   result = await result.json()
  //   console.warn(result)
  //   navigate('/')
  //    }

  return (
    <div className="row row-2">
    <div className="col pb-5">
      <div className="card">
        <Link to={`/details/${product.id}`}>
          <img
            className="card-img-top"
            src={image}
            alt="Card img cap"
            style={{
              height: "15rem",
              width: "12rem",
            }}
          />
           </Link>
          <div className="card-body">
            <h5 className="card-title">
              {title.slice(0, 20)}... - ${price}
            </h5>
            <div className="badge badge-dark text-danger">{category}</div>
            {/* <p className="card-text">{description}</p> */}
            <span
              className="btn btn-danger my-2"
              onClick= {() => deleteCurrentProduct(product._id)}
            >
              Delete
            </span>
            <span
              className="btn btn-warning my-2"
              onClick={deleteCurrentProduct}
            >
              Cart+
            </span>
            <span
              className="btn btn-success my-2"
              onClick={deleteCurrentProduct}
            >
             Update
            </span>
          </div>
       
      </div>
    </div>
    </div>
  );
};

export default Product;
