// export default Addproduct;

export default class Addproduct extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      category: props.category,
      file: props.file,
      title: props.title,
      description: props.description,
      price: props.price,

      category: "",
      file: "",
      title: "",
      description: "",
      price: "",
    };
  }
  updateCategory(event) {
    this.setCategory({
      category: event.target.value,
    });
  }

  updateFile(event) {
    this.setFile({
      file: e.target.value,
    });
  }
  updateTitle(event) {
    this.setTitle({
      title: e.target.value,
    });
  }
  updateDescription(event) {
    this.setDescription({
      description: event.target.value,
    });
  }
  updatePrice(event) {
    this.setPrice({
      price: event.target.value,
    });
  }
  save() {
    var context = this;

    $.add({
      url: "localhost:8000/product",

      method: "POST",

      data: {
        category: context.state.category,

        file: context.state.file,
        title: context.state.title,
        description: context.state.description,
        price: context.state.price,
      },

      success: function (response) {
        alert("Successfully saved record!");
      },

      error: function (response) {
        alert("Error in saving record!");
      },
    });
  }
  render() {
    return (
      <div>
        category:
        <input
          type="text"
          value={this.state.category}
          onChange={this.updateCategory.bind(this)}
        />
        File:
        <input
          type="file"
          value={this.state.file[0]}
          onChange={this.updateFile.bind(this)}
        />
        Title:
        <input
          type="file"
          value={this.state.title}
          onChange={this.updateTitle.bind(this)}
        />
        Description:
        <input
          type="file"
          value={this.state.description}
          onChange={this.updateDescription.bind(this)}
        />
        Price:
        <input
          type="file"
          value={this.state.price}
          onChange={this.updatePrice.bind(this)}
        />
        <button onClick={this.save.bind(this)}>Save</button>
      </div>
    );
  }
}
